import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./home.css";
import fastfood from "../../images/fast-food 2.png";
import outline from "../../images/outline.png";

function Home() {
  const [categoryType, setCategoryType] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchAllCategoriesType();
  }, []);

  const fetchAllCategoriesType = async () => {
    try {
      const response = await axios.get('http://44.196.192.232:3129/api/category/get');
      setCategoryType(response?.data?.data);
      setLoading(false);
    } catch (error) {
      console.log(error);
      setLoading(false);
    }
  };

  const dryFrozenFoods = categoryType.filter(category =>
    category.title.includes("Dry Foods") || category.title.includes("Frozen Foods")
  );

  const cosmeticsProducts = categoryType.filter(category =>
    category.title.includes("Cosmetics Products")
  );

  const handleNavigation = (categoryData, path) => {
    navigate(path, { state: { categoryData } });
  };

  return (
    <div className="home">
      <div className="contain">
        <div className="home-top">
          <h1>All In One Place For Frozen Foods And Cosmetics</h1>
          <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p>
          <Link to="/register" className="logo">
            <button>Sign Up</button>
          </Link>
        </div>

        <div className="home-bottom">
          {/* Visit Store for Dry Frozen Foods */}
          <div className="store">
            <span className="frozen-img"></span>
            <button 
              className="frozenp" 
              onClick={() => handleNavigation(dryFrozenFoods, "/dryfoods/frozenfoods")}
            >
              <img src={fastfood} alt="Dry and Frozen Foods" />
              Visit Store <i className="bx bx-right-arrow-alt"></i>
            </button>
          </div>

          <span></span>

          {/* Visit Store for Cosmetics Products */}
          <div className="store">
            <span className="cosemetic-img"></span>
            <button 
              className="cosemeticp" 
              onClick={() => handleNavigation(cosmeticsProducts, "/cosmeticsproducts")}
            >
              <img src={outline} alt="Cosmetics Products" />
              Visit Store <i className="bx bx-right-arrow-alt"></i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
