import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./shopcheckout.css";
import { Link } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function ShopCheckout() {
  const [addresses, setAddresses] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();
  // const addresses = location.state?.addresses || [];
  const userId = sessionStorage.getItem("userId");

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await axios.get(`http://3.111.163.2:3129/api/address/get/${userId}`);
        // console.log(response?.data?.data, 'addresss')
        setAddresses(response.data.data);
      } catch (err) {
        console.error('Error fetching product details:', err);
      }
    };
    fetchProductDetails();
  }, []);

  const handleEdit = (index) => {
    navigate("/address", {
      state: { address: addresses[index], index, allAddresses: addresses },
    });
  };
  const handleAdd = () => {
    navigate("/address", { state: { allAddresses: addresses } });
  };

  const handleDelete = (add_id) => {
    console.log(add_id, 'add_id');
    // const updatedAddresses = addresses.filter((_, i) => i !== index);
    // navigate("/shopcheckout", { state: { addresses: updatedAddresses } });
  };

  return (
    <>
    <ToastContainer/>


    </>
  );
}

export default ShopCheckout;
