import React, { useEffect, useState } from "react";
import "./shoppingbag.css";
import shoppingImg from "../../images/shopping-first.jpg";
import { Link, useLocation } from 'react-router-dom';
import axios from "axios";

function ShoppingBag() {
    const [quantity, setQuantity] = useState(1);
    const location = useLocation();
    const { productItem } = location.state || {};
    const productId = productItem?.product?._id;  // Assuming product has an _id

    useEffect(() => {
        if (productItem?.product?.quantity) {
            setQuantity(productItem?.product?.quantity);
        }
    }, [productItem]);

    const getShortDescription = (description) => {
        const words = description?.split(" ");
        if (words.length > 20) {
            return words.slice(0, 20).join(" ") + "...";
        }
        return description;
    };

    // Function to handle increasing quantity
    const increaseCount = async () => {
        console.log('inc')
        try {
            const response = await axios.post("http://192.168.1.12:3129/api/cart/quantity", {
                productId: productId,
                action: 'increase',
            });
            if (response.data.success) {
                setQuantity(quantity + 1);
            } else {
                console.error("Error updating quantity", response.data.message);
            }
        } catch (error) {
            console.error("Error increasing quantity", error);
        }
    };

    // Function to handle decreasing quantity
    const decreaseCount = async () => {
        if (quantity > 1) {
            try {
                const response = await axios.post("http://192.168.1.12:3129/api/cart/quantity", {
                    productId: productId,
                    action: 'decrease',
                });
                if (response.data.success) {
                    setQuantity(quantity - 1);
                } else {
                    console.error("Error updating quantity", response.data.message);
                }
            } catch (error) {
                console.error("Error decreasing quantity", error);
            }
        }
    };

    return (
        <div className="shoppingbag">
            <div className="shoppingbag-container">
                <div className="shoppingbag-banner">
                    <h1>Shopping Bag</h1>
                </div>
            </div>
            <div className="shopping-cards container section">
                <div className="shopping-card-first">
                    <div className="shopping-first-img">
                        <p style={{ color: 'black', fontSize: '1.1rem', marginBottom: '10px' }}>{productItem?.product?.name}</p>
                        <img src={shoppingImg} alt="" />
                    </div>
                    <div className="shopping-first-text">
                        <h5>{getShortDescription(productItem?.product?.description)}</h5>
                        <p>${productItem?.product?.price}</p>
                        <div className="shopping-pricing">
                            <button>${productItem?.product?.price}</button>
                            <button onClick={increaseCount}>+</button>
                            <span>{quantity}</span>
                            <button onClick={decreaseCount}>-</button>
                        </div>
                    </div>
                </div>
                <div className="shopping-card-second">
                    <div className="shopping-pricing-total">
                        <div className="shopping-items">
                            <p>Your Bag</p>
                            <p>Subtotal ({quantity} Items):</p>
                        </div>
                        <h5>${(productItem?.product?.price * quantity).toFixed(2)}</h5>
                    </div>
                    <button type="submit">
                        <Link to="/shopcheckout" className="checkout">Checkout</Link>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ShoppingBag;
