import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import "./contactus.css";
import { useNavigate } from "react-router-dom";
import validator from "validator";
import axios from "axios";

function ContactUs() {
  const [formData, setFormData] = useState({
    fullName: "",
    emailOrMobile: "",
    companyName: "",
    subject: "",
    message: "",
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = (fieldValues = formData) => {
    const newErrors = { ...errors };

    if ("fullName" in fieldValues)
      newErrors.fullName = fieldValues.fullName ? "" : "Please fill the name";
    if ("emailOrMobile" in fieldValues)
      newErrors.emailOrMobile = fieldValues.emailOrMobile
        ? ""
        : "Please fill the email or mobile number";
    if ("age" in fieldValues)
      newErrors.age = fieldValues.age ? "" : "Please fill the age";
    if ("gender" in fieldValues)
      newErrors.gender = fieldValues.gender ? "" : "Please select the gender";
    if ("password" in fieldValues)
      newErrors.password = fieldValues.password
        ? ""
        : "Please fill the password";
    if ("confirmPassword" in fieldValues)
      newErrors.confirmPassword = fieldValues.confirmPassword
        ? ""
        : "Please confirm the password";
    if (
      fieldValues.password &&
      fieldValues.confirmPassword &&
      fieldValues.password !== fieldValues.confirmPassword
    ) {
      toast.error("Passwords do not match");
    }

    setErrors(newErrors);
    return Object.values(newErrors).every((x) => x === "");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    validate({ [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      try {
        let dataToSubmit = {
          userName: formData.fullName,
          age: formData.age,
          gender: formData.gender,
          password: formData.password,
          confirmPassword: formData.confirmPassword,
        };
        if (validator.isEmail(formData.emailOrMobile)) {
          dataToSubmit.email = formData.emailOrMobile;
        } else {
          dataToSubmit.mobileNumber = formData.emailOrMobile;
        }
        const response = await axios.post(
          "http://3.111.163.2:3129/api/user/send-message",
          dataToSubmit
        );
        toast.success(response?.data?.message || "Contact registered successfully!", {
          position: "top-right",
          autoClose: 2000,
        });
      } catch (error) {
        if (error.response) {
          const errorMessage = error.response.data?.message;
          toast.error(errorMessage, {
            position: "top-right",
            autoClose: 3000,
          });
        } else {
          toast.error("Network error or server is down. Please try again later.", {
            position: "top-right",
            autoClose: 3000,
          });
          console.error("Error registering user:", error);
        }
      }
    }
  };

  return (
    <div className="contactus">
      <div className="contactus-container">
        <div className="contactus-banner">
          <h1>Get in Touch</h1>
          <div className="contactus-doc">
            <p>contact@technologycal</p>
            <p>(011) 6543 8974 210</p>
          </div>
        </div>
        <div className="forms">
          <form className="contactus-form" onSubmit={handleSubmit}>
            <div className="con-form">
              <div className="first-inputs comm">
                <label>
                  <h4>Full Name *</h4>
                  <input type="text" placeholder="john doe" required/>
                </label>
                <label>
                  <h4>Your Email *</h4>
                  <input type="text" placeholder="example@yourmail.com" required/>
                </label>
              </div>
              <div className="second-inputs comm">
                <label>
                  <h4>On Behalf of *</h4>
                  <input type="text" placeholder="your company name here" required/>
                </label>
                <label>
                  <h4>Subject *</h4>
                  <input type="text" placeholder="How can we help?" required/>
                </label>
              </div>
              <div className="third-inputs comm">
                <label>
                  <h4>Message *</h4>
                  <textarea
                    name=""
                    id=""
                    placeholder="Hello there, I would like to talk about how to..."
                    rows="6"
                    cols="70"
                    required
                  ></textarea>
                </label>
              </div>
              <div className="button-wrapper">
                <button type="submit">Send Message</button>
              </div>
            </div>
          </form>
        </div>
      </div>
      <ToastContainer />
    </div>
  );
}

export default ContactUs;
