import React, { useEffect, useState } from "react";
import "./shoppingbag.css";
import shoppingImg from "../../images/shopping-first.jpg";
import { Link, useLocation } from 'react-router-dom';
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function ShoppingBag() {
    const [quantity, setQuantity] = useState(1);
    const location = useLocation();
    const { productItem } = location.state || {};
    const productId = productItem?.product?._id;
    const userId = sessionStorage.getItem("userId"); 

    useEffect(() => {
        if (productItem?.product?.quantity) {
            setQuantity(productItem.product.quantity); 
        }
    }, [productItem]);

    const getShortDescription = (description) => {
        const words = description?.split(" ");
        if (words.length > 20) {
            return words.slice(0, 20).join(" ") + "...";
        }
        return description;
    };

    // Function to handle increasing or decreasing quantity
    const handleQuantityChange = async (action) => {
        try {
            const response = await axios.put("http://192.168.1.12:3129/api/cart/change", {
                productId: productId,
                operation: action,
                userId: userId
            });
            console.log(response?.data, 'Response from quantity change');

            if (response.data.success) {
                if (action === 'increase') {
                    setQuantity(quantity + 1);
                } else if (action === 'decrease' && quantity > 1) {
                    setQuantity(quantity - 1);
                }
            } else {
                console.error("Error updating quantity", response.data.message);
            }
        } catch (error) {
            console.error("Error updating quantity", error);
        }
    };

    // Calculate subtotal based on quantity and price
    const subtotal = (productItem?.product?.price * quantity).toFixed(2);

    return (
        <>
        <ToastContainer/>
        
        </>
     
    );
}

export default ShoppingBag;
