import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Thunk to handle async add to cart API call
export const addToCart = createAsyncThunk(
  'cart/addToCart',
  async ({ userId, productId, quantity }, { rejectWithValue }) => {
    try {
      const response = await axios.post('http://44.196.192.232:3129/api/cart/add', {
        userId,
        productId,
        quantity,
      });
      return { product: response.data.product, quantity }; // Return the product and quantity
    } catch (err) {
      return rejectWithValue('Failed to add to cart');
    }
  }
);

// Create a slice
const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    status: null,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(addToCart.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(addToCart.fulfilled, (state, action) => {
        state.status = 'succeeded';

        // Check if the product already exists in the cart
        const existingProduct = state.items.find(
          (item) => item.product._id === action.payload.product._id
        );
        
        if (existingProduct) {
          // If product exists, just update its quantity
          existingProduct.quantity += action.payload.quantity;
        } else {
          // If product doesn't exist, add a new entry
          state.items.push({ product: action.payload.product, quantity: action.payload.quantity });
        }
      })
      .addCase(addToCart.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  },
});

export default cartSlice.reducer;
