import React, { useEffect, useState } from "react";
import { Carousel } from "react-responsive-carousel";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from "react-router-dom";
import "./carsouelsection.css";
import axios from "axios";

function Carsouelsection({ subcategories }) {
  // const monthNames = [
  //   "January",
  //   "February",
  //   "March",
  //   "April",
  //   "May",
  //   "June",
  //   "July",
  //   "August",
  //   "September",
  //   "October",
  //   "November",
  //   "December",
  // ];
  // const currentMonth = monthNames[new Date().getMonth()];


  const [getAllProducts, setGetAllProducts] = useState([]);
  const [favorites, setFavorites] = useState([]);

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December",
  ];
  const currentMonth = monthNames[new Date().getMonth()];
  const userId = sessionStorage.getItem("userId");

  useEffect(() => {
    fetchFavorites();
  }, []);

  const fetchFavorites = async () => {
    try {
      const response = await axios.get(`http://44.196.192.232:3129/api/favorite/get/${userId}`);
      const favoriteProducts = response?.data?.data?.products.map((product) => product._id);
      setFavorites(favoriteProducts || []);
    } catch (error) {
      console.log('Error fetching favorites', error);
    }
  };

  const addToFavorites = async (productId) => {
    try {
      const response = await axios.post('http://44.196.192.232:3129/api/favorite/add', {
        userId: userId,
        productId: productId
      });
      if (response?.data?.success) {
        toast.success(response?.data?.message, {
          autoClose: 2000
        });
        setFavorites([...favorites, productId]);
      } else {
        toast.warn(response?.data?.message, {
          autoClose: 2000
        });
      }
    } catch (error) {
      if (error.response?.status === 409) {
        toast.warn(error.response?.data?.message, {
          autoClose: 2000
        });
      } else {
        console.log('something went wrong', error);
        toast.error("An error occurred, please try again later.", {
          autoClose: 2000
        });
      }
    }
  };

  return (
    <>
    <ToastContainer/>
    
    </>
 
  );
}

export default Carsouelsection;
