import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import "./storeFrozenFoodProd.css";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function StoreFrozenFoodProd() {
  const [product, setProduct] = useState({});
  const { storeProId } = useParams();

  useEffect(() => {
    fetchProd();
  }, []);

  const fetchProd = async () => {
    try {
      const response = await axios.get(
        `http://44.196.192.232:3129/api/product/get/${storeProId}`
      );
      setProduct(response?.data?.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleAddToCart = async () => {
    try {
        if (product && product._id) {
            const response = await dispatch(addToCart({ userId, productId: product._id, quantity }));
            if (response?.payload?.success) {
                toast.success(response?.payload?.message || 'Product Added Successfully!', {
                    autoClose: 1000
                });
            } else {
                toast.error(response?.payload?.message || 'Failed to add product to cart.', {
                    autoClose: 1000
                });
            }
        } else {
            toast.error('Product details not available.', {
                autoClose: 1000
            });
        }
    } catch (error) {
        toast.error('An error occurred. Please try again.', {
            autoClose: 1000
        });
    }
};

  return (
    <>
    <ToastContainer/>
    
    </>
 
  );
}

export default StoreFrozenFoodProd;
