import React, { useState } from "react";
import "./payment.css";
import firstPay from "../../images/first-pay.png"; // Visa
import secondPay from "../../images/second-pay.png"; // PayPal
import thirdPay from "../../images/third-pay.png"; // MasterCard
import fourthPay from "../../images/fourth-pay.png"; // Apple Pay
import { loadStripe } from "@stripe/stripe-js";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

// Load Stripe outside of a component's render to avoid re-creating the Stripe object on every render
const stripePromise = loadStripe("your-public-key-here");

function Payment() {
  const [selectedMethod, setSelectedMethod] = useState(null);

  const paymentMethods = [
    { id: 1, name: "Visa", img: firstPay, lastFour: "2109" },
    { id: 2, name: "PayPal", img: secondPay, lastFour: "2109" },
    { id: 3, name: "MasterCard", img: thirdPay, lastFour: "2109" },
    { id: 4, name: "Apple Pay", img: fourthPay, lastFour: "2109" },
  ];

  const handleMethodSelect = (id) => {
    setSelectedMethod(id);
  };

  const handlePayment = () => {
    if (selectedMethod) {
      alert(
        `Opening payment form for ${paymentMethods.find(
          (method) => method.id === selectedMethod
        ).name}`
      );
    } else {
      alert("Please select a payment method");
    }
  };

  return (
    <div className="payment">
      <div className="payment-container">
        <div className="payment-banner">
          <h1>Payment</h1>
        </div>

        <div className="pay-methods container">
          <div className="payment-methods">
            <h2 style={{ color: "black", fontWeight: "500" }}>Payment</h2>

            {paymentMethods.map((method) => (
              <div
                key={method.id}
                className={`payment-option ${
                  selectedMethod === method.id ? "selected" : ""
                }`}
                onClick={() => handleMethodSelect(method.id)}
              >
                <img src={method.img} alt={method.name} />
                <span>************{method.lastFour}</span>
              </div>
            ))}
          </div>

          <div className="payment-summary">
            <div className="orders-summary">
              <div className="order-item">
                <span>Order</span>
                <span>₹ 7,000</span>
              </div>
              <div className="order-item">
                <span>Shipping</span>
                <span>₹ 30</span>
              </div>
              <div className="order-item">
                <span>Total</span>
                <span>₹ 7,030</span>
              </div>
            </div>

            <div className="payment-buttons">
              <button onClick={handlePayment}>Pay</button>
            </div>

            {/* Conditionally Render Payment Form */}
            {selectedMethod === 1 && (
              <VisaForm stripePromise={stripePromise} />
            )}
            {selectedMethod === 2 && <PayPalForm />}
            {selectedMethod === 3 && (
              <MasterCardForm stripePromise={stripePromise} />
            )}
            {selectedMethod === 4 && (
              <ApplePayForm stripePromise={stripePromise} />
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Payment;

/** Stripe Visa and MasterCard Payment Forms */
function VisaForm({ stripePromise }) {
  return <div>Visa Payment Form (integrate Stripe here)</div>;
}

function MasterCardForm({ stripePromise }) {
  return <div>MasterCard Payment Form (integrate Stripe here)</div>;
}

function ApplePayForm({ stripePromise }) {
  return <div>Apple Pay Payment Form (integrate Stripe here)</div>;
}

/** PayPal Payment Form */
function PayPalForm() {
  return (
    <PayPalScriptProvider
      options={{
        "client-id": "your-paypal-client-id",
      }}
    >
      <PayPalButtons
        createOrder={(data, actions) => {
          return actions.order.create({
            purchase_units: [
              {
                amount: {
                  value: "7.03", // Total cost
                },
              },
            ],
          });
        }}
        onApprove={(data, actions) => {
          return actions.order.capture().then(function (details) {
            alert("Transaction completed by " + details.payer.name.given_name);
          });
        }}
      />
    </PayPalScriptProvider>
  );
}
