import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Thunk to handle async add to cart API call
export const getAddedCarts = createAsyncThunk('cart/getAddedCarts', async ({ rejectWithValue }) => {
    try {
      const response = await axios.post('http://44.196.192.232:3129/api/cart/get');
      return response.data;
    } catch (err) {
      return rejectWithValue('Failed to add to cart');
    }
  }
);

// Create a slice
const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    status: null,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getAddedCarts.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(getAddedCarts.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.items.push(action.payload); 
      })
      .addCase(getAddedCarts.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  },
});

export default cartSlice.reducer;
