import React, { useState, useEffect } from "react";
import "./cart.css";
import { Link } from "react-router-dom";
import axios from "axios";

function Cart() {
  const [allProducts, setAllProducts] = useState([]);

  const token = sessionStorage.getItem("token");

  useEffect(() => {
    fetchAllProducts();
  }, []);

  const fetchAllProducts = async () => {
    try {
      const response = await axios.get("http://192.168.1.18:3129/api/cart/get", {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      console.log(response?.data?.data?.products, "response?.data?.data");
      setAllProducts(response?.data?.data);
    } catch (error) {
      console.log("something went wrong", error);
    }
  };

  const handleCartDelete = async (id) => {
    try {
      await axios.delete(`http://3.111.163.2:3129/api/cart/delete/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      fetchAllProducts(); // Refresh cart after deletion
    } catch (error) {
      console.log("something went wrong");
    }
  };

  return (
    <div className="cart">
      <div className="cart-container">
        <div className="cart-banner">
          <h1>Cart</h1>
        </div>

        <div className="card-cart container">
          {allProducts.map((productItem, index) => (
            <div className="cards" key={index}>
              <div className="card-img">
                <img
                  src={productItem?.product?.image || cartImg}
                  alt={productItem?.product?.name}
                />
              </div>
              <div className="card-text">
                <div className="c-text">
                  <h2>{productItem?.product?.name}</h2>
                  <p>{productItem?.product?.description}</p>
                  <p>${productItem?.product?.price}</p>
                  <p>Quantity: {productItem?.quantity}</p>
                </div>
                <Link to="/shoppingbag">
                  <button>Buy Now</button>
                </Link>
              </div>
              <i
                className="bx bx-x"
                onClick={() => handleCartDelete(productItem?._id)}
              ></i>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Cart;
